import React, { useCallback, useEffect, useState } from 'react';
import { Link, useLocation, useParams, useNavigate } from 'react-router-dom';
import Button from '../../components/Button/Button';
import Goods from './Goods/Goods';
import { API_ADDRESS } from '../../apiConfig';
import './Order.scss';

const Order = () => {
  const [cartList, setCartList] = useState([]);
  const [isOrderLoading, setIsOrderLoading] = useState(false);
  const [totalPrice, setTotalPrice] = useState(0);
  const [isTermsChecked, setIsTermsChecked] = useState(false);
  const { pageType } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  let token = localStorage.getItem('TOKEN') || '';

  const fetchCartData = useCallback(async () => {
    const data = await fetch(API_ADDRESS.order_cart, {
      headers: { Authorization: token },
    });
    const res = await data.json();
    if (res.cart_info) {
      setCartList(() => res.cart_info);
      setTotalPrice(() => 0);
      res.cart_info.forEach(({ price, quantity }) => {
        setTotalPrice(prev => prev + price * quantity);
      });
    } else {
      setCartList(() => []);
    }
  }, [token]);

  useEffect(() => {
    if (pageType === 'cart') {
      (async () => {
        setIsOrderLoading(true);
        await fetchCartData();
        setIsOrderLoading(false);
      })();
    } else {
      setCartList(() => location.state.cartList);
      setTotalPrice(() => 0);
      location.state.cartList.forEach(({ price, quantity }) => {
        setTotalPrice(prev => prev + price * quantity);
      });
    }
  }, [fetchCartData, pageType, location]);

  const adjustCart = (id, quantity) => {
    let adjustedList = [];
    let differ = 0;
    let productId = 0;
    cartList.forEach(product => {
      if (product.cart_id === id) {
        differ = quantity - product.quantity;
        productId = product.product_id;
        adjustedList.push({ ...product, quantity: quantity });
      } else {
        adjustedList.push({ ...product });
      }
    });
    setCartList(adjustedList);

    fetch(API_ADDRESS.order_cart, {
      method: 'POST',
      headers: {
        Authorization: token,
      },
      body: JSON.stringify({
        product_id: productId,
        quantity: differ,
      }),
    });
  };

  const adjustTotalPrice = (type, price) => {
    setTotalPrice(prev => (type === 'minus' ? prev - price : prev + price));
  };

  const deleteGoods = id => {
    const [targetToDelete] = cartList.filter(product => product.cart_id === id);
    adjustTotalPrice('minus', targetToDelete.price * targetToDelete.quantity);
    setCartList(cartList.filter(product => product.cart_id !== id));

    fetch(API_ADDRESS.order_cart, {
      method: 'DELETE',
      headers: {
        Authorization: token,
      },
      body: JSON.stringify({
        cart: id,
      }),
    });
  };

  const deleteAllGoods = async () => {
    const targetIndex = cartList.map(product => product.cart_id);
    for (let index of targetIndex) {
      deleteGoods(index);
    }
    setCartList([]);
  };

  const cartlistCheck = async () => {
    if (cartList.length) {
      navigate('/order/check', { state: { cartList } });
    } else {
      window.alert('결제할 상품이 없습니다.');
    }
  };

  const onOrderReal = async () => {
    const { IMP } = window;
    IMP.init('imp47238746');
    const payData = {
      pg: 'html5_inicis',
      pay_method: 'card',
      merchant_uid: '',
      name: '주문명:결제테스트',
      amount: totalPrice,
      buyer_email: '',
      buyer_name: '',
      buyer_tel: '010-1234-5678',
      buyer_addr: '',
      buyer_postcode: '123-456',
    };
    if (isTermsChecked) {
      const orderTable = cartList.map(product => ({
        product_id: product.product_id,
        quantity: product.quantity,
      }));
      await fetch(API_ADDRESS.pay, {
        method: 'POST',
        headers: {
          Authorization: token,
        },
        body: JSON.stringify(orderTable),
      })
        .then(res => res.json())
        .then(
          data => (
            (payData.merchant_uid = data.merchant_uid),
            (payData.name = data.name),
            (payData.buyer_email = data.buyer_email),
            (payData.buyer_name = data.buyer_name),
            (payData.buyer_addr = data.buyer_addr)
          )
        );
      IMP.request_pay(payData, async function (rsp) {
        // callback 로직
        if (rsp.success) {
          const data = await fetch(API_ADDRESS.order_checkout, {
            method: 'POST',
            headers: {
              Authorization: token,
            },
            body: JSON.stringify(orderTable),
          });
          const res = await data.json();
          navigate(`/order/confirm?order=${res.message}`);
          rsp.order = res.message;
          await fetch(API_ADDRESS.pay_result, {
            method: 'POST',
            headers: {
              Authorization: token,
            },
            body: JSON.stringify(rsp),
          });
        } else {
          console.log(rsp.status);
          await fetch(API_ADDRESS.pay_cancel, {
            headers: {
              Authorization: token,
            },
          });
          window.alert('결제가 취소되었습니다.');
        }
      });
    } else {
      window.alert('약관에 동의해주셔야 결제가 가능합니다.');
    }
  };

  const onOrder = async () => {
    if (isTermsChecked) {
      const orderTable = cartList.map(product => ({
        product_id: product.product_id,
        quantity: product.quantity,
      }));

      const data = await fetch(API_ADDRESS.order_checkout, {
        method: 'POST',
        headers: {
          Authorization: token,
        },
        body: JSON.stringify(orderTable),
      });

      const res = await data.json();

      navigate(`/order/confirm?order=${res.message}`);
    } else {
      window.alert('약관에 동의해주셔야 결제가 가능합니다.');
    }
  };

  const onOrderFailed = async () => {
    if (isTermsChecked) {
      const orderTable = cartList.map(product => ({
        product_id: product.product_id,
        quantity: product.quantity,
      }));

      await fetch(API_ADDRESS.order_fail, {
        method: 'POST',
        headers: {
          Authorization: token,
        },
        body: JSON.stringify(orderTable),
      });
      window.alert('비정상적인 오류로 결제에 실패했습니다.');
    } else {
      window.alert('약관에 동의해주셔야 결제가 가능합니다.');
    }
  };

  return (
    <div className="order">
      <div className="pageTitleArea">
        {pageType !== 'cart' && (
          <div className="progressNav">
            <ul className="progress">
              <li className="stepOne">
                <p>01 주택확인</p>
                {pageType === 'cart' && <span className="line" />}
              </li>
              <li className="stepTwo">
                <p>02 신청진행</p>
                {pageType === 'check' && <span className="line" />}
              </li>
              <li className="stepThree">
                <p>03 신청완료</p>
              </li>
            </ul>
          </div>
        )}
        <div className="pageTitle">
          <h1 className="orderTitle">
            {pageType === 'cart' && '신청내역'}
            {pageType === 'check' && '신청진행'}
          </h1>
        </div>
      </div>
      <div className="pageContent">
        <section className="productSection">
          {cartList.length ? (
            !isOrderLoading && (
              <table className="goodsTable">
                <thead className="goodsTableHead">
                  <tr>
                    <th className="tableHeadElement tableHeadImage" />
                    <th className="tableHeadElement tableHeadGoods">
                      <p>주택</p>
                    </th>
                    <th className="tableHeadElement tableHeadQuantity">
                      <p>지역</p>
                    </th>
                    <th className="tableHeadElement tableHeadPrice">
                      <p>상태</p>
                    </th>
                    <th className="tableHeadElement tableHeadButton" />
                  </tr>
                </thead>
                {cartList.map(product => (
                  <Goods
                    key={product.cart_id}
                    product={product}
                    pageType={pageType}
                    deleteGoods={deleteGoods}
                    adjustTotalPrice={adjustTotalPrice}
                    adjustCart={adjustCart}
                  />
                ))}
              </table>
            )
          ) : (
            <div className="nullContentTable">
              <p>신청한 내역이 없습니다.</p>
            </div>
          )}
          {pageType === 'cart' && (
            <div className="cartButtonContainer">
              <div className="cartButtonWrapper">
                {cartList.length ? (
                  <Button btnOnClick={deleteAllGoods}>장바구니 비우기</Button>
                ) : (
                  <Link to="/product-list">
                    <Button>쇼핑 계속하기</Button>
                  </Link>
                )}
              </div>
            </div>
          )}
        </section>
        {pageType !== 'cart' && (
          <div className="orderTotal">
            <div className="orderInfoContainer">
              <div className="mustCheck">
                <input
                  className="checkBox"
                  type="checkbox"
                  value={isTermsChecked}
                  onChange={() => setIsTermsChecked(prev => !prev)}
                />
                <label className="label" htmlFor="agree">
                  신청하실 주택의 신청조건을 명확히 확인하였으며, 
                  이에 동의합니다.
                </label>
              </div>
            </div>
            <div className="orderCheckButtonWrapper">
              <Button btnOnClick={onOrder}>신청하기</Button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};
export default Order;
